package org.digijava.kernel.entity;


import java.io.Serializable;
import java.util.Objects;

public class MessageId implements Serializable {

    private String key;
    private String locale;
    private String siteId;

    // Constructors, getters, and setters

    public MessageId() {
    }

    public MessageId(String key, String locale, String siteId) {
        this.key = key;
        this.locale = locale;
        this.siteId = siteId;
    }

    // Equals and HashCode methods (generated by IDE or manually implemented)

    @Override
    public int hashCode() {
        return Objects.hash(key, locale, siteId);
    }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        MessageId messageId = (MessageId) o;
        return Objects.equals(key, messageId.key) &&
                Objects.equals(locale, messageId.locale) &&
                Objects.equals(siteId, messageId.siteId);
    }

    // Getters and Setters (generated by IDE or manually implemented)
}
