script.lang:
delete from HELP_HELP_TOPICS t where t.helptopicid in (select max (t1.helptopicid) from HELP_HELP_TOPICS t1
left join HELP_HELP_TOPICS st on t1.helptopicid = st.parent_id where st.parent_id is null
group by t1.topickey, t1.bodyeditkey, t1.siteid, t1.moduleinstance, t1.parent_id, t1.topic_type having count(t1.helptopicid) > 1);
