DELETE FROM amp_fields_visibility WHERE name IN ('Costing Activity Id', 'Contribution Amount', 'Contribution Currency', 'Contribution Type of Assistance', 'Contribution Donors', 'Contribution Financing Type', 'Costing Activity Name', 'Costing Assumptions', 'Costing Contribution Gap', 'Costing Donor', 'Costing Due Date', 'Costing Grand Total Contribution', 'Costing Inputs', 'Costing Progress', 'Costing Total Contribution', 'Costing Total Cost', 'Costing Activity Id', 'Costing Tab', 'Add Costing Button', 'Proposed Project Cost', 'Total Costs', 'Grand Total Cost');
DELETE FROM amp_features_templates WHERE feature IN ( SELECT id FROM amp_features_visibility WHERE name IN ('Contribution Report', 'Costing'));
DELETE FROM amp_features_visibility WHERE name IN ('Contribution Report', 'Costing');
DELETE FROM amp_modules_templates WHERE ampModule IN ( SELECT id FROM amp_modules_visibility WHERE name = 'Activity Costing');
DELETE FROM amp_modules_visibility WHERE name = 'Activity Costing';
