DELETE FROM amp_report_hierarchy WHERE columnid IN (SELECT columnid FROM amp_columns WHERE columnname IN ('Costing Donor', 'Grand Total', 'Grand Total Cost'));
DELETE FROM amp_columns WHERE columnname IN ('Costing Donor', 'Grand Total', 'Grand Total Cost');
DROP TABLE IF EXISTS amp_eu_activity_contribution CASCADE; DROP TABLE IF EXISTS amp_eu_activity CASCADE ;
UPDATE amp_global_settings SET settingsvalue = 'true' WHERE settingsname='Recreate the views on the next server restart';
