insert into util_global_settings_possible_ (setting_name, value_id, value_shown) VALUES ('GIS Funding Type', 'donor', 'Donor Fundings');
insert into util_global_settings_possible_ (setting_name, value_id, value_shown) VALUES ('GIS Funding Type', 'regional', 'Regional Fundings');
insert into amp_global_settings (id, settingsname, settingsvalue, possiblevalues, description, section) values ((select max(id)+1 from amp_global_settings),'GIS Funding Type', 'donor', 'v_g_settings_gis_funding_type', 'Calculate GIS map shading according donor or regional fndings', 'general');
CREATE OR REPLACE VIEW "v_g_settings_gis_funding_type" AS SELECT util_global_settings_possible_.value_id AS id, util_global_settings_possible_.value_shown AS value FROM util_global_settings_possible_ WHERE ((util_global_settings_possible_.setting_name)::text = 'GIS Funding Type'::text);
