script.lang:
CREATE OR REPLACE FUNCTION getexchange(currency character, cdate timestamp without time zone)
  RETURNS double precision AS
$BODY$
				declare r double precision;
				declare base_curr char(3);
				 BEGIN
				 select settingsValue into base_curr from amp_global_settings where settingsName='Base Currency';
				 if base_curr is null then 
				 	base_curr := 'USD';
				 end if;
				 if currency=base_curr then return 1;
				 end if;

				 select exchange_rate into r from amp_currency_rate where from_currency_code = base_curr AND to_currency_code=currency and date_trunc('day', exchange_rate_date)=cdate;
				 if r is not null then return r;
				 end if;

				 select exchange_rate into r from amp_currency_rate where from_currency_code = base_curr AND to_currency_code=currency and date_trunc('day', exchange_rate_date)<=cdate order by exchange_rate_date desc limit 1;
				 if r is not null then return r;
				 end if;

				 select exchange_rate into r from amp_currency_rate where from_currency_code = base_curr AND to_currency_code=currency and date_trunc('day', exchange_rate_date)>=cdate order by exchange_rate_date limit 1;
				 if r is not null then return r;
				 end if;


				 select exchange_rate into r from amp_currency_rate where from_currency_code = currency AND to_currency_code=base_curr and date_trunc('day', exchange_rate_date)=cdate;
				 if r is not null then return 1.0/r;
				 end if;

				 select exchange_rate into r from amp_currency_rate where from_currency_code = currency AND to_currency_code=base_curr and date_trunc('day', exchange_rate_date)<=cdate order by exchange_rate_date desc limit 1;
				 if r is not null then return 1.0 / r;
				 end if;

				 select exchange_rate into r from amp_currency_rate where from_currency_code = currency AND to_currency_code=base_curr and date_trunc('day', exchange_rate_date)>=cdate order by exchange_rate_date limit 1;
				 if r is not null then return 1.0 / r;
				 end if;

				 return 1;
				 END;
				$BODY$
  LANGUAGE plpgsql VOLATILE@
