script.lang:
INSERT INTO util_global_settings_possible_ (setting_name, value_id, value_shown) VALUES ('Amounts in Thousands', '0', 'Amounts in Units'), ('Amounts in Thousands', '1', 'Amounts in Thousands'), ('Amounts in Thousands', '2', 'Amounts in Millions') ON CONFLICT DO NOTHING;
CREATE OR REPLACE VIEW v_g_settings_units AS SELECT util_global_settings_possible_.value_id AS id, util_global_settings_possible_.value_shown AS value FROM util_global_settings_possible_ WHERE util_global_settings_possible_.setting_name::text = 'Amounts in Thousands'::text;
UPDATE amp_global_settings SET description='Specifies whether, by default, money amounts will be specified in units, thousands or millions', possiblevalues = 'v_g_settings_amount_units', value_translatable = TRUE WHERE settingsname = 'Amounts in Thousands';
UPDATE amp_global_settings SET settingsvalue = '0' WHERE settingsname = 'Amounts in Thousands' AND settingsvalue='false';
UPDATE amp_global_settings SET settingsvalue = '1' WHERE settingsname = 'Amounts in Thousands' AND settingsvalue='true';
