CREATE SCHEMA IF NOT EXISTS etl;
CREATE TABLE IF NOT EXISTS etl.dg_file_uploaded ( id bigint NOT NULL, path text NOT NULL, file_name text NOT NULL, created_date DATE DEFAULT CURRENT_DATE, CONSTRAINT etl_dg_file_uploaded_pkey PRIMARY KEY (id) );
CREATE SEQUENCE etl.dg_file_uploaded_seq START 1;
CREATE TABLE IF NOT EXISTS etl.dg_file_information ( id bigint NOT NULL, file_name text NOT NULL, records integer, success integer, error integer, status text, message json, created_date DATE DEFAULT CURRENT_DATE, CONSTRAINT etl_dg_file_information_pkey PRIMARY KEY (id) );
CREATE SEQUENCE etl.dg_file_information_seq START 1;
CREATE TABLE IF NOT EXISTS etl.dg_file_record ( id bigint NOT NULL, file_information_id bigint, row integer, content json, status text, error json, amp_id text, created_date DATE DEFAULT CURRENT_DATE, CONSTRAINT etl_dg_file_record_file_information_id_fkey FOREIGN KEY (file_information_id) REFERENCES etl.dg_file_information (id) MATCH SIMPLE );
CREATE SEQUENCE etl.dg_file_record_seq START 1;
