script.lang:
					DROP VIEW IF EXISTS v_agreement_title_code;
			    	CREATE OR REPLACE VIEW v_agreement_title_code AS 
			    	SELECT DISTINCT f.amp_activity_id, ag.title || ' - ' || ag.code as "Agreement Title + Code", ag.id, ag.code AS "Agreement Code", o.name AS "Donor Agency", o.amp_org_id, ag.title as agreement_title 
			    	FROM amp_agreement ag, amp_funding f, amp_organisation o 
  					WHERE f.agreement = ag.id AND f.amp_donor_org_id = o.amp_org_id;
