script.lang:
DROP VIEW IF EXISTS v_contracting_arrangements;
CREATE OR REPLACE VIEW v_contracting_arrangements AS 
SELECT act.amp_activity_id, txt.body, act.amp_activity_id AS act_id, act.draft, act.approval_status AS status, txt.language AS locale
FROM amp_activity act, dg_editor txt
WHERE txt.editor_key::text = act.contracting_arrangements;
