script.lang:
	DROP VIEW IF EXISTS v_countries;
	CREATE OR REPLACE VIEW v_countries AS 
    	SELECT ra.amp_activity_id, getlocationname(countries.country_id) AS location_name, countries.country_id AS location_id, sum(ra.location_percentage) AS location_percentage 
			FROM amp_activity_location ra, amp_location_cache countries
			WHERE (ra.amp_location_id = countries.amp_location_id) AND (countries.country_id IS NOT NULL)
			GROUP BY ra.amp_activity_id, countries.country_id;
