script.lang:
DROP VIEW IF EXISTS v_ni_component_funding CASCADE;
CREATE OR REPLACE VIEW v_ni_component_funding AS SELECT aac.amp_activity_id AS amp_activity_id, f.amp_component_id, f.amp_component_funding_id, f.amp_component_funding_id AS amp_fund_detail_id, f.transaction_type, f.adjustment_type, f.transaction_date, f.transaction_amount, f.exchange_rate AS fixed_exchange_rate, f.currency_id FROM amp_component_funding f JOIN amp_activity_components aac ON f.amp_component_id = aac.amp_component_id;
DROP VIEW IF EXISTS v_ni_component_funding_debug CASCADE;
CREATE OR REPLACE VIEW v_ni_component_funding_debug AS SELECT vdf.amp_activity_id, aa.name AS activity_name, vdf.amp_component_id, ac.title AS component_name, vdf.amp_component_funding_id, vdf.amp_fund_detail_id, vdf.transaction_type, vdf.adjustment_type, adjtype.category_value AS adjustment_type_name, vdf.transaction_date, vdf.transaction_amount, vdf.fixed_exchange_rate, vdf.currency_id, currency.currency_name AS currency_name FROM v_ni_component_funding vdf JOIN amp_activity_version aa ON aa.amp_activity_id = vdf.amp_activity_id LEFT JOIN amp_category_value adjtype ON adjtype.id = vdf.adjustment_type LEFT JOIN amp_components ac ON ac.amp_component_id = vdf.amp_component_id LEFT JOIN amp_currency currency ON currency.amp_currency_id = vdf.currency_id
